create table users(ID NUMBER auto_increment,NAME VARCHAR(20),EMAIL VARCHAR(20),TYPE NUMBER,HISTORY CLOB,CREATED_DATE TIMESTAMP(6) NOT NULL,MODIFIED_DATE TIMESTAMP(6) NOT NULL,ADDRESS_LINE1 VARCHAR(20),ADDRESS_LINE2 VARCHAR(20),PRIMARY KEY(ID));
create table credentials(USER_NAME VARCHAR(20),PASSWORD VARCHAR(20),USER_ID NUMBER,PRIMARY KEY(USER_NAME),FOREIGN KEY(USER_ID) REFERENCES USERS(ID));
create table user_permissions(user_id number,permissions number);